import { Link } from "react-router-dom";
import React,{useState} from 'react';

export default function Song(props) { /* a div resembling a song to hold info in the ReXplore template*/

    let link = `/${props.name}`
    
    const [hover, setHover] = useState(false)
    
    return (
      <Link to={link}>
        <div className = "song" key={props.id}  onMouseEnter={()=> setHover(true)} onMouseLeave={()=> setHover(false)}>
          <div className = "song-info-project" >
          <img className={`play-button ${hover ? "Show" : ""}`} src = "./content/Images-Profile/playbuttonsmall.png"/>
              <div className ="song-number" id = "song-number">{props.id}</div>
              <div className ="song-title">{props.name}</div>
          </div>
              {/* <div className = "song-desc">{props.categories}</div> */}
        </div>
        <div className = "song-desc-box"></div>
      </Link>
    );
    
  }
