import Embed from "../Project-Components/Embed.js"

import {Projects} from "../Project-Components/ProjectData.js"

import Header from "./Header.js"

import React,{useState} from 'react';

import {useLocation }
    from "react-router-dom";

import Image from "../Project-Components/Image.js"

export default function ProjectPage() {

    const { search } = useLocation();
    const query = new URLSearchParams(search);
    const name = query.get("name");

    const currentProject = Projects[name];

    const [hover, setHover] = useState(false)
    
    return (
 
  <div>
     <Header type = {currentProject.category} title = {name} desc = {currentProject.headerTag} src = { "./content/" + currentProject.headerSrc} link = {currentProject.link}/>
     <div className = "page-content">
     <div className = "description-box">{currentProject.desc}</div>
      <div className = "content-flex">
        {currentProject.embeds.map(p => (
            <Embed {...p}/>
          ))
          }
      </div>

      
        <button className="project-entry"  onMouseEnter={()=> setHover(true)} onMouseLeave={()=> setHover(false)}>
          {/* <div className="section-title">{currentProject.type}</div> */}
          <div className = "content-flex">
            {currentProject.images.filter(im => im.tup).map(p => (
              <Image {...p}/>
            ))
            }

            {currentProject.images.map(p => (
              <Image {...p}/>
            ))
            }
          </div>
        </button>
      </div>
      
  </div>
    );
   
}
