import '../css/Profile.css';
import '../css/style.css';
import '../css/Project.css';

import { Link } from "react-router-dom";

import React,{useState} from 'react';

// import CurrentLinks from './currentLinks';

import DiscographyHover from "./DiscographyHover"

export default function ProjectEntry(props) {

    let link = `/${props.name}`

    const [hover, isHover] = useState(false)
    const showBtn = isHover ? "Show" : "";

    return (
        <Link to={link}>
            <button className="section-entry" key={props.id} onMouseEnter={()=> isHover(true)} onMouseLeave={()=> isHover(false)}>
                <img className="entry-image" src={"./content/" + props.src} />
                <div className={`Info ${showIcons}`}></div>
                <div className="entry-title">{props.name}</div>
                <div className="entry-desc">{props.desc}</div>
                
              
            </button>
           
        </Link>

    );

};
