import React, { useState } from "react";

import "./Profile.css";
import './style.css';

import { Projects } from "./Data";
import ProjectEntry from "./ProjectEntry";
import Toggles from "./Toggles.js";
// import { toggleCategory } from "./Toggles.js";



export default function Discography() {
  const allCategories = [...new Set(Projects.map((z) => z.category))];
  const [currentCat, setCurrentCat] = useState("UI/UX Design")

  const currentProjects = Projects.filter((newProject) => {
    return newProject.category === currentCat;
  });

  // console.log("current projects in discography is: ", currentProjects)

  return (
    <div>

      <div className="section-title">Discography</div>
      <Toggles currentCat={currentCat} setCurrentCat={setCurrentCat} allCategories={allCategories} />

      <div className="section-row">
        {/* <div className="profile-section"> */}
          {currentProjects.map(p => (
            <ProjectEntry {...p} />
          ))
          }

        {/* </div> */}
      </div>

    </div>
  );
}
