import { Link } from "react-router-dom";
import React,{useState} from 'react';

import SongDesc from "react";

export default function Song(props) { /* a div resembling a song to hold info in the ReXplore template*/

    let link = `/${props.name}`
    
    const [hover, setHover] = useState(false)

    const [showDesc, setShowDesc] = useState(false); 

    const openDesc = () => { /* change state of modal when image clicked */
          setShowDesc(true);
    };
    
    return (
      <div>
        <button className = "song" key={props.id}  onMouseEnter={()=> setHover(true)} onMouseLeave={()=> setHover(false)}>
          <div className = "song-info-project" >
          <img className= {`play-button ${hover ? "Show" : ""}`} onClick={openDesc} src = "./content/Images-Profile/playbuttonsmall.png"/>
              
              <div className ="song-number" id = "song-number">{props.id}</div>
              <div className ="song-title">{props.name}</div>
          </div>
        </button>
            {showDesc ? <SongDesc setShowDesc={setShowDesc} img = {"./content/" + props.src} /> : null}
              {/* <div className = "song-desc">{props.categories}</div> */}
      </div>
    
    );
    
  }
