import Overview from "./Overview"
import ResearchMethods from "./ResearchMethods"
import Evidence from "./Evidence"
import Insights from "./Insights"

import Header from "../Pages/Header.js"

import {useLocation }
    from "react-router-dom";


export default function ReXplore() {

  const { search } = useLocation();
    const query = new URLSearchParams(search);
    const name = query.get("name");

    const currentProject = Projects[name];

  return (

    <div>
      <Header type = {currentProject.category} title = {name} desc = {currentProject.headerTag} src = { "./content/" + currentProject.headerSrc} link = {currentProject.link}/>
      <div className = "page-content">
        <Overview/>
        <ResearchMethods />
        <Evidence />
        <Insights />
      </div>
    </div>

  );
}
