import '../css/Profile.css';
import '../css/style.css';
import '../css/Project.css';

import { Link } from "react-router-dom";

import React,{useState} from 'react';

// import currentLinks from './currentLinks';


export default function ProjectEntry(props) {

    let link = `/Project?name=${props.name}`

    const [hover, setHover] = useState(false)

    return (
        <Link to={link}>
            <button className="section-entry" key={props.id} onMouseEnter={()=> setHover(true)} onMouseLeave={()=> setHover(false)}>
                <img className="entry-image" src={"./content/" + props.src} />
                <img className={`play-entry-button ${hover ? "Show" : ""}`} src = "./content/Images-Profile/playbutton.png"/>
                <div className="entry-title">{props.name}</div>
                <div className="entry-desc">{props.desc}</div> 
            </button>
           
        </Link>

    );

};
