import '../css/Project.css';

import React, { useRef } from "react";
import ReactDom from "react-dom";

export const SongDesc = ({ setShowDesc, img, desc}) => {
  const modalRef = useRef();
  
  const closeDesc = (e) => {
    if (e.target === modalRef.current) {
      setShowDesc(false);
    }
  };

  return ReactDom.createPortal(

    <div className="song-desc-container" ref={modalRef} onClick={closeDesc}>
      <div className="modal">
        <img class = "modal-img" src = {img}></img>
        <p>{desc}</p>
        <img className="close-modal-button" src = "./content/Images-Profile/closebutton.png" onClick={() => setShowDesc(false)}/>
      </div>
    </div>,
   
    document.getElementById("song-description")
  );
}
