import '../css/Project.css';

import React, { useRef } from "react";
import ReactDom from "react-dom";

export const SongDesc = ({ setShowDesc, img}) => {
  const modalRef = useRef();
  
  const closeDesc = (e) => {
    if (e.target === modalRef.current) {
      setShowDesc(false);
    }
  };

  return ReactDom.createPortal(

    // <div className="song-desc-box" ref={modalRef} onClick={closeDesc}>
    //     <img className = "song-desc-img" src = {img} />
    //     {/* <p>{props.desc}</p> */}
    // </div>,
    <div className="container" ref={modalRef} onClick={closeModal}>
      <div className="modal">
        <img class = "modal-img" src = {img}></img>
        <img className="close-modal-button" src = "./content/Images-Profile/closebutton.png" onClick={() => setShowModal(false)}/>
      </div>
    </div>,
    
    document.getElementById("song-description")
  );
}
