import React, { useState } from "react";

import "./Profile.css";
import './style.css';

import {Projects} from "./Data";
import ProjectEntry from "./FeaturingEntry.js";
import Toggles from "./Toggles.js";
// import { toggleCategory } from "./Toggles.js";



export default function Discography() {

  console.log("projects are: ", Projects)


  const allCategories  = [...new Set(Projects.map((z) => z.category))];

  // const allCategories = Projects.map(x => x.category)

  console.log("all categories are: ", allCategories)

  // const [category, setCategory] = useState(Projects);
  // let allcats = ["UI/UX Design", "Product Design", "Info Design"]

  const [currentCat, setCurrentCat] = useState("UI/UX Design")

  const currentProjects = Projects.filter((newProject) => {
    return newProject.category === currentCat; 
  });

  return (
    <div>

      <div class = "section-title">Discography</div>
      <Toggles currentCat={currentCat} setCurrentCat allCategories/>

    <div class = "section-row">
      <div class = "profile-section">
      {currentProjects.map(p => (
        <ProjectEntry props={p}/> 
      ))}
         
      </div>
    </div>

    </div>
  );
}
